    }, timeout);
  }
  return (
    <>
      <NoteState>
      <Router>
        <Navbar />
        <Alert message="Woww! that's Alert"/>
      <div className= "container">
        <Switch>
        <Route exact path="/">
            <Home/>
          </Route>
          <Route exact path="/about">
            <About/>
          </Route>
          <Route exact path="/login">
            <Login/>
          </Route>
          <Route exact path="/signup">
            <Signup/>
          </Route>
        </Switch>
        </div>
      </Router>
      </NoteState>
    </>
  );
}

export default App;
