// import { useState } from "react";
import NoteContext from "./notes/noteContext";
import { useState } from "react";

const NoteState = (props) => {
  const host = "http://localhost:5000";
  const notesInitial = [];
  const [notes, setNotes] = useState(notesInitial);

  //Get all Notes
  const getNotes = async () => {
    //API CALL
    const response = await fetch(`${host}/api/notes/addnote/`, {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        "auth-token":
          "eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJ1c2VyIjp7ImlkIjoiNjE1MmMxYzQ0MDc2N2I0YzRmNmVhNzA5In0sImlhdCI6MTYzMjgxMzUwOH0.tppekgz-Ztj1cyCEwp2CYG93NbhuBKXW8dNfcxAvXaw",
      },

      body: JSON.stringify({ title, description, tag }),
    });

    console.log("adding a new note");
    const note = {
      _id: "6153eb5f79437c0488096685cb",
      user: "6152c1c440767b4c4f6ea1709",
      title: title,
      description: description,
      tag: tag,
      date: "2021-09-29T04:28:04.168Z",
      __v: 0,
    };
    setNotes(notes.concat(note));
    //here concat return new array.
  };

  //Delete a Note
  const deleteNote = (id) => {
    //TODO: API CALL
    console.log("Deleting the note with id" + id);
    const newNotes = notes.filter((note) => {
      return note._id !== id;
    });
    setNotes(newNotes);
  };

  //Edit a Note
  const editNote = async (id, title, description, tag) => {
    //API CALL
    const response = await fetch(`${host}/api/notes/updatenote/${id}`, {
      method: "POST",

      headers: {
        "Content-Type": "application/json",
        "auth-token":
          "eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJ1c2VyIjp7ImlkIjoiNjE1MmMxYzQ0MDc2N2I0YzRmNmVhNzA5In0sImlhdCI6MTYzMjgxMzUwOH0.tppekgz-Ztj1cyCEwp2CYG93NbhuBKXW8dNfcxAvXaw",
      },

      body: JSON.stringify({ title, description, tag }),
    });
    const json = response.json();

    //Logic to edit in client
    for (let index = 0; index < notes.length; index++) {
      const element = notes[index];
      if (element._id === id) {
        element.title = title;
        element.description = description;
        element.tag = tag;
      }
    }
  };

  // const s1 = {
  //     "name": "shashank",
  //     "class": "4a",
  // }

  // const [state, setstate] = useState(s1);
  // const update = ()=>{
  //     setTimeout(() => {
  //         setstate({
  //             "name": "Siddharth",
  //             "class": "A1"
  //         })
  //     }, 3000);
  // }
  return (
    <NoteContext.Provider
      value={{ notes, setNotes, addNote, deleteNote, editNote }}
    >
      {props.children}
    </NoteContext.Provider>
  );
};
export default NoteState;
